mapper.select:
select record_id, record_number, record_date, temp_morning, temp_afternoon,
record_cough, references_name, other_cough, other_name, other_situation,
outside_life, outside_reason ,health, fever,cough,weak ,r.remark from sys_record r
left join sys_user u on r.record_number = u.login_name
where u.dept_id = #{deptId} and u.student = false
 and record_number like concat('%', #{recordNumber}, '%')
 and record_date = #{recordDate}

    AND date_format(r.record_date,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


    AND date_format(r.record_date,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

 and r.temp_afternoon = #{tempAfternoon}
 and r.record_cough = #{recordCough}
 and r.references_name like concat('%', #{referencesName}, '%')
 and r.other_cough = #{otherCough}
 and r.other_name like concat('%', #{otherName}, '%')
 and r.other_situation = #{otherSituation}
 and r.outside_life = #{outsideLife}
 and r.outside_reason = #{outsideReason}
and r.health = #{health}
and r.fever = #{fever}
and r.cough = #{cough}
and r.weak = #{weak}


    and( temp_morning > 37.3 or temp_afternoon > 37.3)
    and temp_morning <= 37.3 and temp_afternoon <= 37.3
    and temp_morning = #{tempMorning}

select record_id, record_number, record_date, temp_morning, temp_afternoon,
record_cough, references_name, other_cough, other_name, other_situation,
outside_life, outside_reason ,health, fever,cough,weak ,remark from sys_record r
where r.record_number in (
select u.login_name from sys_user u
left join sys_user_role r on u.user_id = r.user_id
where r.role_id = 100
    and u.dept_id = #{dept.deptId}
    and u.zy_name = #{user.zyName}
    and u.grade_id = #{user.gradeId}
)
 and record_number like concat('%', #{recordNumber}, '%')
 and record_date = #{recordDate}

    AND date_format(r.record_date,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


    AND date_format(r.record_date,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')



    and( temp_morning > 37.3 or temp_afternoon > 37.3)
    and temp_morning <= 37.3 and temp_afternoon <= 37.3
    and temp_morning = #{tempMorning}

 and temp_afternoon = #{tempAfternoon}
 and record_cough = #{recordCough}
 and references_name like concat('%', #{referencesName}, '%')
 and other_cough = #{otherCough}
 and other_name like concat('%', #{otherName}, '%')
 and other_situation = #{otherSituation}
 and outside_life = #{outsideLife}
 and outside_reason = #{outsideReason}
and health = #{health}
and fever = #{fever}
and cough = #{cough}
and weak = #{weak}

select record_id, record_number, record_date, temp_morning, temp_afternoon,
record_cough, references_name, other_cough, other_name, other_situation,
outside_life, outside_reason ,health, fever,cough,weak ,remark from sys_record r
where r.record_number in (
select u.login_name from sys_user u
left join sys_user_role r on u.user_id = r.user_id
where r.role_id = 102 and u.student = false
and u.dept_id = #{dept.deptId}
)
 and record_number like concat('%', #{recordNumber}, '%')
 and record_date = #{recordDate}

    AND date_format(r.record_date,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


    AND date_format(r.record_date,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')



    and( temp_morning > 37.3 or temp_afternoon > 37.3)
    and temp_morning <= 37.3 and temp_afternoon <= 37.3
    and temp_morning = #{tempMorning}

 and temp_afternoon = #{tempAfternoon}
 and record_cough = #{recordCough}
 and references_name like concat('%', #{referencesName}, '%')
 and other_cough = #{otherCough}
 and other_name like concat('%', #{otherName}, '%')
 and other_situation = #{otherSituation}
 and outside_life = #{outsideLife}
 and outside_reason = #{outsideReason}
and health = #{health}
and fever = #{fever}
and cough = #{cough}
and weak = #{weak}

mapper.update:
update sys_record
record_number = #{recordNumber}, record_date = #{recordDate}, temp_morning = #{tempMorning}, temp_afternoon = #{tempAfternoon}, record_cough = #{recordCough}, references_name = #{referencesName}, other_cough = #{otherCough}, other_name = #{otherName}, other_situation = #{otherSituation}, outside_life = #{outsideLife}, outside_reason = #{outsideReason}, health = #{health}, fever = #{fever}, cough = #{cough}, weak = #{weak}, remark = #{remark},
where record_id = #{recordId}
