mapper.select:
select distinct u.user_id, u.dept_id, u.login_name, u.user_name, u.email, u.avatar, u.phonenumber, u.status, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.login_name like concat('%', #{loginName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}

mapper.update:
update sys_user
dept_id = #{deptId}, login_name = #{loginName}, user_name = #{userName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, salt = #{salt}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, student = #{student}, parents_name = #{parentsName}, parents_phnu = #{parentsPhnu}, province = #{province}, city_name = #{cityName}, county = #{county}, address = #{address}, graduates = #{graduates}, on_job = #{onJob}, workplace = #{workplace}, after_wuhan = #{afterWuhan}, contact_wuhan = #{contactWuhan}, grade_id = #{gradeId}, college_id = #{collegeId}, card_nu = #{cardNu}, update_time = sysdate()
where user_id = #{userId}
