mapper.select:
select distinct u.college_id from sys_user u where u.student = 1 and u.college_id is not null
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE FIND_IN_SET (#{deptId},ancestors) ))
and u.zy_name = #{zyName}

select distinct u.grade_id from sys_user u where u.student = 1 and u.grade_id is not null
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE FIND_IN_SET (#{deptId},ancestors) ))
and u.zy_name = #{zyName} and u.college_id = #{collegeId}

select u.user_id, u.dept_id, u.login_name, u.user_name, u.email, u.avatar, u.phonenumber, u.password, u.sex, u.salt, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, u.student, u.parents_name, u.parents_phnu, u.province, u.city_name, u.county, u.address, u.graduates, u.on_job, u.workplace, u.after_wuhan, u.contact_wuhan, u.grade_id, u.card_nu, u.zy_name, u.college_id, d.dept_name, d.leader from sys_user u left join sys_dept d on u.dept_id = d.dept_id where u.del_flag = '0'
AND u.login_name like concat('%', #{loginName}, '%')
AND u.status = #{status}
AND u.student = #{student}
AND u.user_name like concat('%', #{userName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
AND date_format(u.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
AND date_format(u.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE FIND_IN_SET (#{deptId},ancestors) ))
${params.dataScope}

select distinct u.user_id, u.dept_id, u.login_name, u.user_name, u.email, u.avatar, u.phonenumber, u.status, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.login_name like concat('%', #{loginName}, '%')
AND u.user_name like concat('%', #{userName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}

mapper.update:
update sys_user
dept_id = #{deptId}, login_name = #{loginName}, user_name = #{userName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, salt = #{salt}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, student = #{student}, parents_name = #{parentsName}, parents_phnu = #{parentsPhnu}, province = #{province}, city_name = #{cityName}, county = #{county}, address = #{address}, graduates = #{graduates}, on_job = #{onJob}, workplace = #{workplace}, after_wuhan = #{afterWuhan}, contact_wuhan = #{contactWuhan}, grade_id = #{gradeId}, card_nu = #{cardNu}, college_id = #{collegeId}, zy_name = #{zyName}, update_time = sysdate()
where user_id = #{userId}
